import React, { useEffect, useState } from 'react';
import './OrangeScrollControl.css'; // Import the CSS file for styling

const OrangeScrollControl = () => {
  const [scrollPercentage, setScrollPercentage] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const windowHeight = window.innerHeight;
      const documentHeight = document.documentElement.scrollHeight;
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const maxScrollTop = documentHeight - windowHeight;
      const scrollPercentage = (scrollTop / maxScrollTop) * 100;
      setScrollPercentage(scrollPercentage);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="orange-scroll-control">
      <div
        className="orange-scroll-control__indicator"
        style={{ height: `${scrollPercentage}%` }}
      />
    </div>
  );
};

export default OrangeScrollControl;
